import React, { useState, useEffect } from "react";
import { Layout } from "./../components/Layout/Layout";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";
import Rating from "@mui/material/Rating";
import Skeleton from "react-loading-skeleton";
import "react-loading-skeleton/dist/skeleton.css";

const ProductDetails = () => {
  const params = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState({});
  const [relatedProducts, setRelatedProducts] = useState([]);

  //initalp details
  useEffect(() => {
    if (params.slug) getProduct();
  }, [params.slug]);
  //getProduct
  const getProduct = async () => {
    try {
      const { data } = await axios.get(
        `/api/v1/product/get-product/${params.slug}`
      );
      setProduct(data.product);
      getSimilarProduct(data.product._id, data.product.category._id);
    } catch (error) {
      console.log(error);
    }
  };
  //get similar product
  const getSimilarProduct = async (pid, cid) => {
    try {
      const { data } = await axios.get(
        `/api/v1/product/related-product/${pid}/${cid}`
      );
      setRelatedProducts(data.products);
    } catch (error) {
      console.log(error);
    }
  };
  return (
    <Layout>
      <div className="row container product-details">
        <div className="col-md-6">
          <img
            src={`/api/v1/product/product-photo/${product._id}`}
            className="card-img-top"
            alt={product.name}
            height="300"
            width={"350px"}
          />
        </div>
        <div className="col-md-6 product-details-info">
          <h1 className="text-center">Product Details</h1>
          <hr />
          <h6>Name : {product.name}</h6>
          <h6>Description : {product.description}</h6>
          <h6>Price :{product.price}</h6>

          <button class="btn btn-secondary ms-1">ADD TO CART</button>
        </div>
      </div>
      <hr />
      <div className="row container similar-products">
        <h4>Similar Products ➡️</h4>
        {relatedProducts.length < 1 && (
          <p className="text-center">No Similar Products found</p>
        )}
        <div className="grid grid-cols-1 gap-10 p-2 sm:p-6 md:grid-cols-2 lg:grid-cols-3 lg:p-28 lg:pt-2 lg:pb-6">
          {relatedProducts.length
            ? relatedProducts.map((p) => (
                <div
                  key={p._id}
                  className="relative w-full max-w-sm rounded-3xl bg-[var(--black-color)] shadow-md"
                >
                  <div className="relative flex h-60 overflow-hidden rounded-t-3xl">
                    <img
                      className="object-cover w-full"
                      src={`/api/v1/product/product-photo/${p._id}`}
                      alt="product image"
                    />
                    <span className="absolute top-0 left-0 m-2 rounded-full bg-black px-2 text-center text-sm font-medium text-white">
                      39% OFF
                    </span>
                  </div>
                  <div className="mt-4 px-5 pb-5">
                    <button onClick={() => navigate(`/product/${p.slug}`)}>
                      <h5 className="text-xl tracking-tight text-white font-bold">
                        {p.name}
                      </h5>
                    </button>
                    <div className="mt-2 mb-5 flex items-center justify-between">
                      <p>
                        <span className="text-3xl font-semibold text-white">
                          {p.price.toLocaleString("en-US", {
                            style: "currency",
                            currency: "INR",
                          })}
                        </span>
                      </p>
                      {/* Rating */}
                      <div className="flex items-center mt-2.5 mb-5">
                        <Rating name="read-only" value={5} readOnly />
                        <span className="bg-blue-100 text-blue-800 text-xs font-semibold mr-2 px-2.5 py-0.5 rounded dark:bg-blue-200 dark:text-blue-800 ml-3">
                          5.0
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              ))
            : // Display loading skeletons if products are not available
              Array(6)
                .fill()
                .map((_, index) => (
                  <div
                    key={index}
                    className="relative w-full max-w-sm rounded-3xl bg-white shadow-md"
                  >
                    <Skeleton height={280} borderRadius={"1.5rem"} />
                    <div className="mt-4 px-5 pb-5">
                      <Skeleton count={1} />
                      <br />
                      <Skeleton height={24} width={80} />
                      <br />
                      <Skeleton count={2} />
                    </div>
                  </div>
                ))}
        </div>
      </div>
    </Layout>
  );
};

export default ProductDetails;
