import React from "react";
import { Layout } from "../components/Layout/Layout";
import Carousel from "../components/Carousel";

const HomePage = () => {
  return (
    <Layout>
      <div className="items-center">
        {/* Carousel */}
        <Carousel />

        <div className="p-6 grid grid-cols-1 gap-6 md:grid-cols-3 md:p-20">
          <button className="relative bg-gradient-to-r from-sky-500 to-indigo-500 rounded-[30px] w-full h-[200px]">
            <div className="absolute text-xl ml-6 left-0 top-1/3 font-extrabold uppercase rounded-br-[30px]">
              Oversized T-Shirts
            </div>
            <div className="absolute ml-6 left-0 top-1/2 font-medium text-sm uppercase rounded-[30px] bg-white px-4 py-2">
              Shop Now
            </div>
          </button>
          <button className="relative bg-gradient-to-r from-sky-500 to-indigo-500 rounded-[30px] w-full h-[200px]">
            <div className="absolute left-0 top-0 p-6 bg-white rounded-br-[30px]">
              T-Shirts
            </div>
          </button>
          <button className="relative bg-gradient-to-r from-sky-500 to-indigo-500 rounded-[30px] w-full h-[200px]">
            <div className="absolute left-0 top-0 p-6 bg-white rounded-br-[30px]">
              Jeans
            </div>
          </button>
        </div>
      </div>
    </Layout>
  );
};

export default HomePage;
