import React from "react";
import { Layout } from "../../components/Layout/Layout";
import { useAuth } from "../../context/auth";
import Admin from "../../components/Layout/Admin";

const AdminDashboard = () => {
  const [auth] = useAuth();
  return (
    <Layout>
      <div className="pt-32 pb-10 px-6 text-center flex justify-center">
        <h1 className="text-4xl font-bold pb-2"> Dashboard </h1>

          <div className="justify-center max-w-sm bg-white border border-gray-200 rounded-lg shadow dark:bg-gray-800 dark:border-gray-700">
            <div className="flex flex-col items-center pb-10 pt-10">
              <img
                className="w-24 h-24 mb-3 rounded-full shadow-lg bg-cover"
                src="https://lh3.googleusercontent.com/a/ACg8ocIE190z0-O3FGhbvddiCZX52vQpoZFgFLb3th68-wbxCTw=s360-c-no"
                alt="Bonnie image"
              />
              <h5 className="mb-1 text-xl font-medium text-gray-900 dark:text-white text capitalize">
                {auth.user.name}
              </h5>
              <span className="text-sm text-gray-500 dark:text-gray-400">
                {auth.user.email}
              </span>
              <span className="text-sm text-gray-500 dark:text-gray-400">
                {auth.user.phone}
              </span>
              <div className="flex mt-4 space-x-3 md:mt-6">
                <Admin />
              </div>
            </div>
          </div>
        </div>
      
    </Layout>
  );
};

export default AdminDashboard;
