import React, { useState, useEffect } from "react";
import { Layout } from "../components/Layout/Layout";
import axios from "axios";
import Rating from "@mui/material/Rating";
import { Prices } from "../components/Search_Filter/PriceFilter";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import KeyboardArrowDownIcon from "@mui/icons-material/KeyboardArrowDown";
import Checkbox from "@mui/material/Checkbox";
import Radio from "@mui/material/Radio";
import { FormControlLabel } from "@mui/material";
import CurrencyRupeeIcon from "@mui/icons-material/CurrencyRupee";
import CategoryIcon from "@mui/icons-material/Category";
import { useCart } from "../context/cart";
import toast from "react-hot-toast";
import Carousel from "../components/Carousel";
import Skeleton from "react-loading-skeleton";
import "react-loading-skeleton/dist/skeleton.css";
import { useNavigate } from "react-router-dom";

const HomePage = () => {
  const [cart, setCart] = useCart();
  const [products, setProducts] = useState([]);
  const [Categories, setCategories] = useState([]);
  const [anchorEl, setanchorEl] = React.useState(null);
  const [anchorEll, setanchorEll] = React.useState(null);
  const [checked, setChecked] = useState([]);
  const [radio, setRadio] = useState([]);

  const navigate = useNavigate();

  const openCategory = Boolean(anchorEl);
  const handleClickCategory = (event) => {
    setanchorEl(event.currentTarget);
  };
  const handleCloseCategory = () => {
    setanchorEl(null);
  };

  const openPrice = Boolean(anchorEll);
  const handleClickPrice = (event) => {
    setanchorEll(event.currentTarget);
  };
  const handleClosePrice = () => {
    setanchorEll(null);
  };

  //Get All Category
  const getAllCategories = async () => {
    try {
      const { data } = await axios.get("/api/v1/category/get-category");
      if (data.success) {
        setCategories(data.category);
      } else {
        console.log("Error in getting Category");
      }
    } catch (error) {
      console.log(error);
    }
  };

  //Get all Categories
  useEffect(() => {
    getAllCategories();
  }, []);

  //Filter by Category
  const handleFilter = (value, id) => {
    let all = Array.isArray(checked) ? [...checked] : [];
    if (value) {
      all.push(id);
    } else {
      all = all.filter((c) => c !== id);
    }
    setChecked(all);
  };

  //Get all Products
  const getAllProducts = async () => {
    try {
      const { data } = await axios.get(`/api/v1/product/get-product`);
      setProducts(data.products);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getAllProducts();
  }, []);

  //Get Filtered Product
  const filterProduct = async () => {
    try {
      const { data } = await axios.post(`/api/v1/product/product-filter`, {
        checked,
        radio,
      });
      setProducts(data.products);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    if (!checked.length || !radio.length) getAllProducts();
  }, [checked.length, radio.length]);

  useEffect(() => {
    if (checked.length || radio.length) filterProduct();
  }, [checked, radio]);

  return (
    <Layout>
      <div className="p-2 items-center">
        {/* Carousel */}
        <Carousel />

        <div className="items-center">
          <div className="flex gap-6 flex-col items-start sm:flex-row sm:item-center justify-between m-6">
            <div className="text-5xl text-center font-extrabold tracking-tight leading-none text-black md:text-6xl lg:text-6xl">
              <h1>All Products </h1>
            </div>
            {/* Filters */}
            <div className="flex flex-col gap-2 items-start sm:flex-row sm:items-center justify-end sm:gap-6 w-fit rounded-3xl">
              <div>
                <h4>Filter by</h4>
              </div>

              <div className="flex flex-row gap-2">
                {/* Filter by Category */}
                <div>
                  <button
                    type="submit"
                    className="p-2 px-3 text-sm font-medium text-center text-white rounded-lg bg-indigo-600 sm:w-fit hover:bg-indigo-800 dark:bg-indigo-600 dark:hover:bg-indigo-600 "
                    onClick={handleClickCategory}
                  >
                    <CategoryIcon /> Categories <KeyboardArrowDownIcon />
                  </button>
                  <Menu
                    iid="basic-menu"
                    anchorEl={anchorEl}
                    open={openCategory}
                    onClose={handleCloseCategory}
                    MenuListProps={{
                      "aria-labelledby": "basic-button",
                    }}
                  >
                    {Categories.map((c) => (
                      <MenuItem>
                        <FormControlLabel
                          control={
                            <Checkbox
                              key={c._id}
                              onChange={(e) => {
                                handleFilter(e.target.checked, c._id);
                              }}
                            />
                          }
                          label={c.name}
                        />{" "}
                      </MenuItem>
                    ))}
                  </Menu>
                </div>

                {/* Filter by Price */}
                <div>
                  {" "}
                  <button
                    type="submit"
                    className="p-2 px-3 text-sm font-medium text-center text-white rounded-lg bg-indigo-600 sm:w-fit hover:bg-indigo-600 dark:bg-indigo-600 dark:hover:bg-indigo-600 "
                    onClick={handleClickPrice}
                  >
                    <CurrencyRupeeIcon /> Price <KeyboardArrowDownIcon />
                  </button>
                  <Menu
                    iid="basic-menu"
                    anchorEl={anchorEll}
                    open={openPrice}
                    onClose={handleClosePrice}
                    MenuListProps={{
                      "aria-labelledby": "basic-button",
                    }}
                  >
                    {Prices.map((p) => (
                      <MenuItem key={p._id}>
                        <FormControlLabel
                          control={
                            <Radio onChange={(e) => setRadio(e.target.value)} />
                          }
                          label={p.name}
                        />{" "}
                      </MenuItem>
                    ))}
                  </Menu>
                </div>
              </div>
            </div>
          </div>
          {/* Products */}
          <div className="grid grid-cols-1 gap-10 p-2 sm:p-28 sm:pt-2 sm:grid-cols-3 md:grid-cols-2">
            {products.map((p) => (
              <div className="w-full max-w-sm bg-white rounded-3xl shadow dark:bg-gray-800 dark:border-gray-700">
                <img
                  className="pb-6 rounded-t-3xl"
                  src={`/api/v1/product/product-photo/${p._id}`}
                  alt="product image"
                />
                <div className="px-5 pb-5">
                  <button onClick={() => navigate(`/product/${p.slug}`)}>
                    <h5 className="text-xl font-semibold tracking-tight text-gray-900 dark:text-white uppercase">
                      {p.name}{" "}
                    </h5>
                  </button>
                  {/* Rating */}
                  <div className="flex items-center mt-2.5 mb-5">
                    <Rating name="read-only" value={5} readOnly />
                    <span className="bg-blue-100 text-blue-800 text-xs font-semibold mr-2 px-2.5 py-0.5 rounded dark:bg-blue-200 dark:text-blue-800 ml-3">
                      5.0
                    </span>
                  </div>
                  <div className="flex items-center justify-between">
                    <span className="text-3xl font-bold text-gray-900 dark:text-white">
                      {p.price.toLocaleString("en-US", {
                        style: "currency",
                        currency: "INR",
                      })}
                    </span>
                    <button
                      className="text-white bg-indigo-600 hover:bg-indigo-600 font-medium rounded-lg text-sm px-5 py-2.5 text-center dark:bg-indigo-600 dark:hover:bg-indigo-600 "
                      onClick={() => {
                        const existingProductIndex = cart.findIndex(
                          (item) => item._id === p._id
                        );

                        if (existingProductIndex !== -1) {
                          // If the product is already in the cart, update its quantity
                          const updatedCart = [...cart];
                          updatedCart[existingProductIndex].quantity += 1;

                          setCart(updatedCart);
                        } else {
                          // If the product is not in the cart, add it as a new item with a quantity of 1
                          setCart([...cart, { ...p, quantity: 1 }]);
                        }

                        // Save the updated cart to localStorage
                        localStorage.setItem(
                          "cart",
                          JSON.stringify([...cart, p])
                        );

                        toast.success(`${p.name} Added to Cart`);
                      }}
                    >
                      Add to cart
                    </button>
                  </div>
                </div>
              </div>
            )) || <Skeleton count={10} />}
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default HomePage;
