import React from "react";
import Header from "./Header";
import Footer from "./Footer";
import { ToastContainer } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';

export const Layout = ({ children }) => {
  return (
    <div>
      <Header />
      <main>
        <ToastContainer />
        {children}
      </main>
      <Footer />
    </div>
  );
};
