import * as React from "react";
import Box from "@mui/material/Box";
import Slider from "@mui/material/Slider";

const Price = [
  {
    value: 0,
  },
  {
    value: 200,
  },
  {
    value: 400,
  },
  {
    value: 600,
  },
  {
    value: 800,
  },
  {
    value: 1000,
  },
];

function valuetext(value) {
  return `${value}`;
}

export default function PriceFilter() {
  return (
    <Box sx={{ width: 300 }}>
      <h4 className="text-medium text-white font-bold mb-2">Filter by Price:</h4>
      <Slider
        defaultValue={600}
        getAriaValueText={valuetext}
        step={1000}
        marks={Price}
        valueLabelDisplay="on"
      />
    </Box>
  );
}
